mapper.select:
SELECT COUNT(*)
FROM dutch_record
WHERE id = #{id}

SELECT *
FROM dutch_record
WHERE id = #{recordId} AND trip_id = #{tripId}

SELECT COUNT(*)
FROM dutch_record
WHERE trip_id = #{tripId}
AND currency = #{currency}


    AND (content LIKE #{pattern} OR location LIKE #{pattern})

SELECT *
FROM dutch_record
WHERE trip_id = #{tripId}
AND currency = #{currency}


    AND (content LIKE #{pattern} OR location LIKE #{pattern})

ORDER BY `date` DESC
LIMIT #{limit} OFFSET #{offset}

mapper.delete:
DELETE
FROM dutch_record
WHERE id = #{id} AND trip_id = #{tripId}
