SELECT a.member_id, b.currency, SUM(a.actual) AS actual, SUM(a.should) AS should
FROM dutch_record_member a
INNER JOIN dutch_record b ON b.id = a.record_id
WHERE b.trip_id = #{tripId}
GROUP BY a.member_id, b.currency;
