mapper.select:
SELECT *
FROM dutch_record_member
WHERE record_id = #{recordId} AND member_id = #{memberId}

SELECT a.member_id, SUM(a.actual) AS actual, SUM(a.should) AS should
FROM dutch_record_member a
INNER JOIN dutch_record b ON b.id = a.record_id
WHERE b.trip_id = #{tripId}
GROUP BY a.member_id;
