mapper.select:
SELECT COUNT(*)
FROM album
WHERE id = #{id}

SELECT a.id, a.name, a.thumbnail_photo_id, a.reg_dt
FROM album a
INNER JOIN album_owner b ON b.album_id = a.id

    AND a.thumbnail_photo_id = #{thumbnailPhotoId}
    AND b.user_id = #{userId}
    AND b.owner = #{owner}

ORDER BY b.reg_dt DESC

    LIMIT #{page.pageSize} OFFSET #{page.offset}

SELECT a.id, a.name, a.thumbnail_photo_id, a.reg_dt
FROM album a
WHERE a.id = #{id}

SELECT a.id, a.name, a.thumbnail_photo_id, a.reg_dt
FROM album_owner ao
INNER JOIN album a ON a.id = ao.album_id

    AND ao.album_id = #{albumId}
    AND ao.user_id = #{userId}
    AND ao.owner = #{owner}

mapper.update:
UPDATE album
SET name = #{name},
    thumbnail_photo_id = #{thumbnailPhotoId}
WHERE id = #{id}
