    LIMIT #{page.pageSize} OFFSET #{page.offset}

SELECT b.*
FROM album_owner a
INNER JOIN album b ON b.id = a.album_id
WHERE a.user_id = #{userId} AND a.album_id = #{albumId}

    AND a.owner = #{owner}
