mapper.select:
SELECT album_id, photo_id, user_id, reg_dt
FROM album_photo

    AND album_id = #{albumId}
    AND photo_id = #{photoId}
    AND user_id = #{userId}

        AND album_id IN #{v}

SELECT COUNT(*)
FROM album_photo

    AND album_id = #{albumId}
    AND photo_id = #{photoId}
    AND user_id = #{userId}

SELECT album_id, photo_id, user_id, reg_dt
FROM album_photo
WHERE album_id = #{albumId} AND photo_id = #{photoId}

SELECT COUNT(*)
FROM album_photo
WHERE album_id = #{albumId} AND photo_id < #{photoId}

mapper.update:
UPDATE album_photo
SET photo_id = #{to}
WHERE photo_id = #{from}
