mapper.select:
SELECT album_id, photo_id, user_id, reg_dt
FROM album_photo

    AND album_id = #{albumId}

        AND album_id IN (#{v})


        AND photo_id NOT IN (#{v})



    LIMIT #{page.pageSize} OFFSET #{page.offset}

SELECT album_id, photo_id, user_id, reg_dt
FROM album_photo
WHERE album_id = #{albumId} AND photo_id = #{photoId}

SELECT COUNT(*)
FROM album_photo
WHERE album_id = #{albumId} AND photo_id < #{photoId}

mapper.delete:
DELETE FROM album_photo
WHERE photo_id IN (#{v})
  AND user_id = #{userId}
