mapper.select:
SELECT a.*
FROM photo a
INNER JOIN photo_owner b ON b.photo_id = a.id

    AND b.user_id = #{userId}
    AND a.id BETWEEN #{idRange.start} AND #{idRange.endInclusive}
    AND a.hash = #{hash}
    AND a.id < #{idLessThan}
    AND a.id > #{idGreaterThan}

ORDER BY a.id
ORDER BY a.id DESC

    LIMIT #{page.pageSize} OFFSET #{page.offset}

SELECT count(a.*)
FROM photo a
INNER JOIN photo_owner b ON b.photo_id = a.id

    AND b.user_id = #{userId}
    AND a.id BETWEEN #{idRange.start} AND #{idRange.endInclusive}
    AND a.hash = #{hash}

SELECT a.*
FROM photo a
INNER JOIN album_photo b ON b.photo_id = a.id
WHERE b.album_id = #{albumId}
LIMIT #{page.pageSize} OFFSET #{page.offset}

SELECT a.*
FROM photo a
INNER JOIN photo_owner b ON b.photo_id = a.id
WHERE a.id = #{id}
AND b.user_id = #{userId}

SELECT COUNT(*)
FROM photo
WHERE id = #{id}
