mapper.select:
SELECT user_id, photo_id, name, file_dt, reg_dt, date, offset, date_type, photo_id_new
FROM photo_owner

    AND user_id = #{userId}
    AND photo_id = #{photoId}


        AND name LIKE #{namePattern}



    LIMIT #{page.pageSize} OFFSET #{page.offset}

SELECT COUNT(*)
FROM photo_owner

    AND user_id = #{userId}
    AND photo_id = #{photoId}


        AND name LIKE #{namePattern}

SELECT user_id, photo_id, name, file_dt, reg_dt, date, offset, date_type, photo_id_new
FROM photo_owner
WHERE photo_id = #{photoId} AND user_id = #{userId}

mapper.update:
UPDATE photo_owner
SET photo_id = #{to}
WHERE photo_id = #{from}

mapper.delete:
DELETE FROM photo_owner
WHERE
photo_id = #{photoId} AND user_id = #{userId}
